  &lt;label&gt;Enter a URL:&lt;/label&gt;
  &lt;input type=&quot;url&quot; /&gt;
  &lt;br /&gt;
  &lt;br /&gt;
  &lt;label&gt;Enter an email address:&lt;/label&gt;
  &lt;input type=&quot;email&quot; required/&gt;
